import React, { useState, useCallback, useRef } from 'react';
import Cropper from 'react-easy-crop';
import './ImageEditorWithMenu.css';

import getCroppedImg from './utils/cropImage';

const ImageEditorWithMenu = () => {
  const inputRef = useRef();
  const [image, setImage] = useState(null);
  const [crop, setCrop] = useState({ x: 0, y: 0 });
  const [zoom, setZoom] = useState(1);
  const [rotation, setRotation] = useState(0);
  const [flipX, setFlipX] = useState(false);
  const [opacity, setOpacity] = useState(1);
  const [croppedAreaPixels, setCroppedAreaPixels] = useState(null);
  const [menuOpen, setMenuOpen] = useState(false);

  const onCropComplete = useCallback((_, croppedPixels) => {
    setCroppedAreaPixels(croppedPixels);
  }, []);

  const handleUpload = (e) => {
    const file = e.target.files[0];
    if (!file) return;
    const reader = new FileReader();
    reader.onload = () => setImage(reader.result);
    reader.readAsDataURL(file);
  };

  const saveImage = async () => {
    const cropped = await getCroppedImg(image, croppedAreaPixels, rotation, flipX, opacity);
    const a = document.createElement('a');
    a.href = cropped;
    a.download = 'edited-image.png';
    a.click();
  };

  return (
    <div className="editor-container">
      {/* Burger Button */}
      <button className="burger" onClick={() => setMenuOpen(!menuOpen)}>
        â˜°
      </button>

      {/* Sidebar Menu */}
      <div className={`sidebar ${menuOpen ? 'open' : ''}`}>
        <label>
          Upload Image:
          <input type="file" accept="image/*" onChange={handleUpload} ref={inputRef} />
        </label>

        <label>
          Zoom:
          <input type="range" min="1" max="3" step="0.1" value={zoom} onChange={(e) => setZoom(e.target.value)} />
        </label>

        <label>
          Rotation:
          <input type="range" min="0" max="360" step="1" value={rotation} onChange={(e) => setRotation(e.target.value)} />
        </label>

        <label>
          Opacity:
          <input type="range" min="0.1" max="1" step="0.1" value={opacity} onChange={(e) => setOpacity(e.target.value)} />
        </label>

        <label>
          Flip:
          <input type="checkbox" checked={flipX} onChange={() => setFlipX(!flipX)} />
        </label>

        <button onClick={saveImage} className="btn btn-success mt-3">Save</button>
      </div>

      {/* Crop Area */}
      <div className="crop-wrapper" style={{ opacity }}>
        {image && (
          <Cropper
            image={image}
            crop={crop}
            zoom={zoom}
            rotation={rotation}
            onCropChange={setCrop}
            onCropComplete={onCropComplete}
            onZoomChange={setZoom}
            style={{
              containerStyle: { borderRadius: '10px', overflow: 'hidden' },
              mediaStyle: { transform: flipX ? 'scaleX(-1)' : 'scaleX(1)' }
            }}
          />
        )}
      </div>
    </div>
  );
};

export default ImageEditorWithMenu;
